import { useEffect, useState } from "react";
import axios from "axios";

function HomePage() {
  const user = JSON.parse(localStorage.getItem("user"));
  const [time, setTime] = useState(new Date());
  const [waveData, setWaveData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const latitude = 32.08; // ◊™◊ú ◊ê◊ë◊ô◊ë
  const longitude = 34.78;

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 60000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);

      try {
        const res = await axios.get(
          "https://marine-api.open-meteo.com/v1/marine",
          {
            params: {
              latitude,
              longitude,
              hourly: "wave_height,wind_speed,wind_direction",
            },
          }
        );

        // ◊©◊û◊ô◊®◊î ◊®◊ß ◊ê◊ù ◊ë◊ê◊û◊™ ◊ó◊ñ◊®◊ï ◊õ◊ú ◊î◊©◊ì◊ï◊™
        if (
          res.data.hourly &&
          res.data.hourly.wave_height &&
          res.data.hourly.wind_speed &&
          res.data.hourly.wind_direction
        ) {
          setWaveData(res.data.hourly);
        } else {
          throw new Error("◊†◊™◊ï◊†◊ô ◊™◊ó◊ñ◊ô◊™ ◊ó◊°◊®◊ô◊ù ◊û◊î÷æAPI");
        }
      } catch (err) {
        console.error("◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊™◊ó◊ñ◊ô◊™ ◊î◊ô◊ù:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const getWaveLabel = (height) => {
    if (height <= 0.6) return "◊ë◊®◊ö";
    if (height <= 1.2) return "◊ó◊ñ◊î";
    if (height <= 2.0) return "◊®◊ê◊©";
    return "◊û◊¢◊ú ◊®◊ê◊©";
  };

  const kmhToKnots = (kmh) => Math.round(kmh / 1.852);

  const getWindDirection = (degrees) => {
    if (degrees >= 45 && degrees < 135) return "◊®◊ï◊ó ◊û◊ñ◊®◊ó◊ô◊™ (Onshore)";
    if (degrees >= 135 && degrees < 225) return "◊®◊ï◊ó ◊ì◊®◊ï◊û◊ô◊™";
    if (degrees >= 225 && degrees < 315) return "◊®◊ï◊ó ◊û◊¢◊®◊ë◊ô◊™ (Offshore)";
    return "◊®◊ï◊ó ◊¶◊§◊ï◊†◊ô◊™";
  };

  const getLevelRecommendation = (waveHeight, windSpeedKnots, windDirText) => {
    if (waveHeight <= 0.6 && windSpeedKnots < 10) {
      return "◊û◊ï◊û◊ú◊• ◊ú◊û◊™◊ó◊ô◊ú◊ô◊ù üü¢";
    }
    if (waveHeight <= 1.0 && windSpeedKnots < 12) {
      return "◊û◊ï◊û◊ú◊• ◊ú◊°◊ê◊§◊ô◊ù üõ∂";
    }
    if (waveHeight > 0.6 && waveHeight <= 1.5 && windSpeedKnots < 15) {
      return "◊û◊ï◊û◊ú◊• ◊ú◊û◊™◊ß◊ì◊û◊ô◊ù üü°";
    }
    if (waveHeight > 1.0 && windDirText.includes("Offshore")) {
      return "◊û◊ï◊û◊ú◊• ◊ú◊°◊ï◊§◊ò◊ô◊ù üèÑ‚Äç‚ôÇÔ∏è";
    }
    return "◊ú◊ê ◊û◊™◊ê◊ô◊ù ◊õ◊®◊í◊¢ üî¥";
  };

  const getForecastForNextHours = () => {
    if (!waveData) return [];

    const currentHourISO = time.toISOString().slice(0, 13);
    const startIndex = waveData.time.findIndex((t) =>
      t.startsWith(currentHourISO)
    );
    if (startIndex === -1) return [];

    return waveData.time.slice(startIndex, startIndex + 6).map((t, i) => {
      const height = Math.round(waveData.wave_height[startIndex + i] * 10) / 10;
      const windSpeedKmh = waveData.wind_speed[startIndex + i];
      const windSpeedKnots = kmhToKnots(windSpeedKmh);
      const windDirDegrees = waveData.wind_direction[startIndex + i];
      const windDirText = getWindDirection(windDirDegrees);

      const date = new Date(t);
      const formattedTime = `${date.getDate().toString().padStart(2, "0")}/${(
        date.getMonth() + 1
      )
        .toString()
        .padStart(2, "0")} ${date.getHours().toString().padStart(2, "0")}:00`;

      return {
        formattedTime,
        heightLabel: getWaveLabel(height),
        windSpeedKnots,
        windDirText,
        rec: getLevelRecommendation(height, windSpeedKnots, windDirText),
      };
    });
  };

  const forecast = getForecastForNextHours();

  return (
    <div className="container">
      <h2>◊ë◊®◊ï◊ö ◊î◊ë◊ê ◊ú÷æBlueLine üåä</h2>

      <h3>üåä ◊™◊ó◊ñ◊ô◊™ ◊ú÷æ6 ◊©◊¢◊ï◊™ ◊î◊ß◊®◊ï◊ë◊ï◊™</h3>
      {loading ? (
        <p>◊ò◊ï◊¢◊ü ◊†◊™◊ï◊†◊ô ◊ô◊ù...</p>
      ) : error ? (
        <p>◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊†◊™◊ï◊†◊ô◊ù: {error}</p>
      ) : forecast.length ? (
        <ul>
          {forecast.map((f, i) => (
            <li key={i}>
              <strong>{f.formattedTime}</strong> ‚Äì ◊í◊ï◊ë◊î ◊í◊ú◊ô◊ù: {f.heightLabel},{" "}
              ◊®◊ï◊ó◊ï◊™: {f.windSpeedKnots} ◊ß◊©◊® ({f.windDirText}) üí° {f.rec}
            </li>
          ))}
        </ul>
      ) : (
        <p>◊ú◊ê ◊†◊û◊¶◊ê◊ï ◊†◊™◊ï◊†◊ô◊ù ◊¢◊ë◊ï◊® ◊î◊©◊¢◊ï◊™ ◊î◊ß◊®◊ï◊ë◊ï◊™</p>
      )}
    </div>
  );
}

export default HomePage;
