import axios from "axios";
import { useEffect, useState } from "react";

export default function HomePage() {
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchForecast = async () => {
      try {
        const now = new Date();
        const start = now.toISOString().split(".")[0] + "Z";
        const endDate = new Date(now);
        endDate.setHours(endDate.getHours() + 24); // תחזית ל-24 שעות
        const end = endDate.toISOString().split(".")[0] + "Z";

        const response = await axios.get(
          "https://marine-api.open-meteo.com/v1/marine",
          {
            params: {
              latitude: 32.08, // תל אביב לדוגמה
              longitude: 34.78,
              hourly: "wave_height,wind_speed,wind_direction,sea_state",
              start,
              end,
            },
          }
        );

        setForecast(response.data);
      } catch (err) {
        setError(err.message);
        console.error("שגיאה בטעינת תחזית הים:", err);
      }
    };

    fetchForecast();
  }, []);

  if (error) return <div>שגיאה בטעינת תחזית הים: {error}</div>;
  if (!forecast) return <div>טוען תחזית ים...</div>;

  return (
    <div>
      <h1>תחזית ים ל-24 שעות</h1>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>שעה</th>
            <th>גובה גלים (מ')</th>
            <th>מהירות רוח (קמ"ש)</th>
            <th>כיוון רוח (°)</th>
            <th>מצב ים</th>
          </tr>
        </thead>
        <tbody>
          {forecast.hourly.time.map((time, index) => (
            <tr key={time}>
              <td>
                {new Date(time).toLocaleTimeString([], {
                  hour: "2-digit",
                  minute: "2-digit",
                })}
              </td>
              <td>{forecast.hourly.wave_height[index]}</td>
              <td>{forecast.hourly.wind_speed[index]}</td>
              <td>{forecast.hourly.wind_direction[index]}</td>
              <td>{forecast.hourly.sea_state[index]}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
