import React, { useEffect, useState } from "react";
import axios from "axios";

const HomePage = () => {
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchForecast = async () => {
      try {
        // קוד יום של היום
        const today = new Date().toISOString().split("T")[0];

        const response = await axios.get(
          `https://marine-api.open-meteo.com/v1/marine`,
          {
            params: {
              latitude: 32.08, // תל אביב
              longitude: 34.78,
              hourly: "wave_height,wind_speed,wind_direction,sea_state",
              start_date: today,
              end_date: today,
            },
          }
        );

        setForecast(response.data.hourly);
      } catch (err) {
        console.error("שגיאה בטעינת תחזית הים:", err);
        setError("לא ניתן לטעון את תחזית הים כרגע.");
      }
    };

    fetchForecast();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h1>דף הבית</h1>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {forecast ? (
        <div>
          <h2>תחזית יומית</h2>
          <table border="1" cellPadding="5">
            <thead>
              <tr>
                <th>שעה</th>
                <th>גובה גלים (מ׳)</th>
                <th>מהירות רוח (קמ״ש)</th>
                <th>כיוון רוח (°)</th>
                <th>מצב הים</th>
              </tr>
            </thead>
            <tbody>
              {forecast.time.map((time, index) => (
                <tr key={time}>
                  <td>{time.split("T")[1]}</td>
                  <td>{forecast.wave_height[index]}</td>
                  <td>{forecast.wind_speed[index]}</td>
                  <td>{forecast.wind_direction[index]}</td>
                  <td>{forecast.sea_state[index]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        !error && <p>טוען תחזית...</p>
      )}
    </div>
  );
};

export default HomePage;
