import { useEffect, useState } from "react";
import axios from "axios";

function WaveForecast() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  const latitude = 32.08; // תל אביב
  const longitude = 34.78;

  // המרת גובה גלים לטקסט (ברך / חזה / ראש)
  const getWaveLabel = (height) => {
    if (height <= 0.6) return "ברך";
    if (height <= 1.2) return "חזה";
    if (height <= 2.0) return "ראש";
    return "מעל ראש";
  };

  // פונקציה להמרת כיוון רוח ממעלות (0-360) לטקסט כללי
  const getWindDirection = (degrees) => {
    if (degrees >= 45 && degrees < 135) return "רוח מזרחית (Onshore)";
    if (degrees >= 135 && degrees < 225) return "רוח דרומית";
    if (degrees >= 225 && degrees < 315) return "רוח מערבית (Offshore)";
    return "רוח צפונית";
  };

  // פונקציה להמרת עוצמת רוח מקמ"ש לקשרים (knots)
  const kmhToKnots = (kmh) => {
    return Math.round(kmh / 1.852); // 1 knot = 1.852 km/h
  };

  // המלצה לרמות גלישה
  const getLevelRecommendation = (waveHeight, windSpeedKnots, windDirText) => {
    if (waveHeight <= 0.6 && windSpeedKnots < 10) {
      return "מומלץ למתחילים 🟢";
    }
    if (waveHeight <= 1.0 && windSpeedKnots < 12) {
      return "מומלץ לסאפים 🛶";
    }
    if (waveHeight > 0.6 && waveHeight <= 1.5 && windSpeedKnots < 15) {
      return "מומלץ למתקדמים 🟡";
    }
    if (waveHeight > 1.0 && windDirText.includes("Offshore")) {
      return "מומלץ לסופטים 🏄‍♂️";
    }
    return "לא מתאים כרגע 🔴";
  };

  // פונקציה לחישוב המלצה כללית
  const getSurfRecommendation = (waveHeight, windSpeedKnots, windDir) => {
    let score = 0;
    let message = "";
    let icon = "";

    if (waveHeight >= 0.5 && waveHeight <= 2.5) {
      score += 2;
      message += "גלים טובים ";
    } else if (waveHeight > 2.5) {
      message += "גלים גבוהים מדי ";
    } else {
      message += "גלים נמוכים ";
    }

    if (windSpeedKnots < 15) {
      score += 2;
      message += "רוח מתונה ";
    } else {
      message += "רוח חזקה ";
    }

    const isOffshore = windDir.includes("Offshore");
    if (isOffshore) {
      score += 1;
      message += "כיוון offshore ";
    } else {
      message += "כיוון onshore ";
    }

    if (score >= 4) {
      icon = "✅ טוב לגלישה!";
    } else if (score >= 2) {
      icon = "⚠️ בינוני – בדוק שוב";
    } else {
      icon = "❌ לא מומלץ";
    }

    const stars = "⭐".repeat(score);
    return { score, stars, message, icon };
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(
          `https://marine-api.open-meteo.com/v1/marine?latitude=${latitude}&longitude=${longitude}&hourly=wave_height,wind_speed,wind_direction`
        );
        setData(res.data.hourly);
      } catch (error) {
        console.error("שגיאה בטעינת תחזית הים", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <p>טוען נתוני ים...</p>;
  if (!data) return <p>לא הצלחנו לטעון תחזית.</p>;

  return (
    <div>
      <h3>תחזית גלים ורוח לתל אביב 🌊</h3>
      <ul>
        {data.time.slice(0, 6).map((time, index) => {
          const waveHeight = Math.round(data.wave_height[index] * 10) / 10;
          const waveLabel = getWaveLabel(waveHeight);
          const windSpeedKmh = data.wind_speed[index];
          const windSpeedKnots = kmhToKnots(windSpeedKmh);
          const windDirDegrees = data.wind_direction[index];
          const windDirText = getWindDirection(windDirDegrees);

          const rec = getSurfRecommendation(
            waveHeight,
            windSpeedKnots,
            windDirText
          );
          const levelRec = getLevelRecommendation(
            waveHeight,
            windSpeedKnots,
            windDirText
          );

          const date = new Date(time);
          const formattedTime = `${date
            .getDate()
            .toString()
            .padStart(2, "0")}/${(date.getMonth() + 1)
            .toString()
            .padStart(2, "0")} ${date
            .getHours()
            .toString()
            .padStart(2, "0")}:00`;

          return (
            <li key={time}>
              <strong>{formattedTime}</strong> – גובה גלים: {waveLabel}, רוחות:{" "}
              {windSpeedKnots} קשר ({windDirText}), המלצה: {rec.stars}{" "}
              {rec.icon} ({rec.message}) → {levelRec}
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default WaveForecast;
