import { useEffect, useState } from "react";
import axios from "axios";

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch all users
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const { data } = await axios.get("/api/users", {
        headers: { Authorization: localStorage.getItem("token") },
      });
      setUsers(data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  // Toggle Ban / Unban
  const toggleBan = async (id) => {
    try {
      await axios.put(
        `/api/users/${id}/ban`,
        {},
        {
          headers: { Authorization: localStorage.getItem("token") },
        }
      );
      fetchUsers();
    } catch (err) {
      console.error(err);
    }
  };

  // Make Admin
  const makeAdmin = async (id) => {
    try {
      await axios.put(
        `/api/users/${id}/make-admin`,
        {},
        {
          headers: { Authorization: localStorage.getItem("token") },
        }
      );
      fetchUsers();
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  if (loading) return <p>Loading users...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Admin Panel</h1>
      <table
        border="1"
        cellPadding="8"
        style={{ width: "100%", textAlign: "left" }}
      >
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Banned</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>{user.isBanned ? "Yes" : "No"}</td>
              <td>
                <button onClick={() => toggleBan(user._id)}>
                  {user.isBanned ? "Unban" : "Ban"}
                </button>
                {user.role !== "admin" && (
                  <button onClick={() => makeAdmin(user._id)}>
                    Make Admin
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
