import { useEffect, useState } from "react";
import axios from "axios";

function HomePage() {
  const user = JSON.parse(localStorage.getItem("user"));
  const [time, setTime] = useState(new Date());
  const [waveData, setWaveData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const latitude = 32.08; // תל אביב
  const longitude = 34.78;

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 60000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);

      const getFormattedDate = () => {
        const today = new Date();
        const yyyy = today.getFullYear();
        const mm = String(today.getMonth() + 1).padStart(2, "0");
        const dd = String(today.getDate()).padStart(2, "0");
        return `${yyyy}-${mm}-${dd}`;
      };

      const formattedDate = getFormattedDate();

      try {
        const res = await axios.get(
          "https://marine-api.open-meteo.com/v1/marine",
          {
            params: {
              latitude,
              longitude,
              hourly: "wave_height,wave_direction,wind_speed",
              start_date: formattedDate,
              end_date: formattedDate,
            },
          }
        );

        setWaveData(res.data.hourly);
      } catch (error) {
        console.error("שגיאה בטעינת תחזית הים:", error);
        setError(error.response ? error.response.data : "שגיאה לא ידועה");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const hour = time.getHours();
  const date = time.toLocaleDateString("he-IL");
  const currentTime = time.toLocaleTimeString("he-IL", {
    hour: "2-digit",
    minute: "2-digit",
  });

  let greeting = "שלום";
  let emoji = "🌊";
  let bgColor = "#f2f4f8";

  if (hour >= 5 && hour < 12) {
    greeting = "בוקר טוב";
    emoji = "☀";
    bgColor = "#e0f7fa";
  } else if (hour >= 12 && hour < 18) {
    greeting = "צהריים טובים";
    emoji = "🌇";
    bgColor = "#fff3e0";
  } else {
    greeting = "ערב טוב";
    emoji = "🌙";
    bgColor = "#ede7f6";
  }

  // === פונקציות עזר לתצוגה ===
  const getWaveLabel = (height) => {
    if (height <= 0.6) return "ברך";
    if (height <= 1.2) return "חזה";
    if (height <= 2.0) return "ראש";
    return "מעל ראש";
  };

  const kmhToKnots = (kmh) => Math.round(kmh / 1.852);

  const getWindDirection = (degrees) => {
    if (degrees >= 45 && degrees < 135) return "רוח מזרחית (Onshore)";
    if (degrees >= 135 && degrees < 225) return "רוח דרומית";
    if (degrees >= 225 && degrees < 315) return "רוח מערבית (Offshore)";
    return "רוח צפונית";
  };

  const getLevelRecommendation = (waveHeight, windSpeedKnots, windDirText) => {
    if (waveHeight <= 0.6 && windSpeedKnots < 10) {
      return "מומלץ למתחילים 🟢";
    }
    if (waveHeight <= 1.0 && windSpeedKnots < 12) {
      return "מומלץ לסאפים 🛶";
    }
    if (waveHeight > 0.6 && waveHeight <= 1.5 && windSpeedKnots < 15) {
      return "מומלץ למתקדמים 🟡";
    }
    if (waveHeight > 1.0 && windDirText.includes("Offshore")) {
      return "מומלץ לסופטים 🏄‍♂️";
    }
    return "לא מתאים כרגע 🔴";
  };

  const getCurrentSeaCondition = () => {
    if (!waveData) return null;

    const currentHourISO = time.toISOString().slice(0, 13);
    const index = waveData.time.findIndex((t) => t.startsWith(currentHourISO));
    if (index === -1) return null;

    const height = Math.round(waveData.wave_height[index] * 10) / 10;
    const windSpeedKmh = waveData.wind_speed[index];
    const windSpeedKnots = kmhToKnots(windSpeedKmh);
    const windDirDegrees = waveData.wave_direction[index];
    const windDirText = getWindDirection(windDirDegrees);

    return {
      height,
      heightLabel: getWaveLabel(height),
      windSpeedKnots,
      windDirText,
      rec: getLevelRecommendation(height, windSpeedKnots, windDirText),
    };
  };

  const sea = getCurrentSeaCondition();

  return (
    <div className="container" style={{ backgroundColor: bgColor }}>
      <h2>ברוך הבא ל־BlueLine {emoji}</h2>

      {user ? (
        <>
          <p>
            {greeting}, {user.name}! שמחים לראות אותך שוב 🏄‍♂
          </p>
          <p>📅 תאריך: {date}</p>
          <p>⏰ שעה נוכחית: {currentTime}</p>
        </>
      ) : (
        <p>ברוך הבא אורח – נא להתחבר או להירשם</p>
      )}

      <hr />

      <h3>🌊 תנאי הים של היום</h3>
      {loading ? (
        <p>טוען נתוני ים...</p>
      ) : error ? (
        <p>שגיאה בטעינת נתונים: {error.message || "לא ניתן לטעון נתוני ים"}</p>
      ) : sea ? (
        <>
          <p>גובה הגלים: {sea.heightLabel}</p>
          <p>רוחות: {sea.windSpeedKnots} קשר</p>
          <p>כיוון הרוח: {sea.windDirText}</p>
          <p>💡 {sea.rec}</p>
        </>
      ) : (
        <p>לא ניתן לטעון נתוני ים עבור השעה הנוכחית</p>
      )}

      <hr />

      <h3>🔗 קישורים נוספים</h3>
      <ul>
        <li>
          <a href="/courses">🎓 קורסים לגלישה</a>
        </li>
        <li>
          <a href="/blog">📰 בלוג גולשים</a>
        </li>
        <li>
          <a href="/trips">🌴 טיולי גלישה</a>
        </li>
      </ul>
    </div>
  );
}

export default HomePage;
