// frontend/src/pages/BlogPage.jsx

import { useState, useEffect } from "react";
import { io } from "socket.io-client";
// β… Χ™Χ™Χ‘Χ•Χ Link Χ›Χ“Χ™ ΧΧΧ¤Χ©Χ¨ Χ Χ™Χ•Χ•Χ ΧΧΧ•Χ Χ”Χ§ΧΧ¨Χ“
import { Link } from "react-router-dom";
import "./BlogPage.css";

function BlogPage() {
  const [posts, setPosts] = useState([]);

  // β… Χ‘Χ“Χ™Χ§Χ” ΧΧ Χ”ΧΧ©ΧΧΧ© ΧΧ—Χ•Χ‘Χ¨
  const user = JSON.parse(localStorage.getItem("user"));
  const isLoggedIn = !!user; // Χ™Χ”Χ™Χ” true ΧΧ Χ™Χ© ΧΧ©ΧΧΧ©

  useEffect(() => {
    // ... Χ”Χ§Χ•Χ“ Χ”Χ§Χ™Χ™Χ Χ©Χ dummyPosts
    const dummyPosts = [
      // ... Χ¤Χ•Χ΅ΧΧ™Χ Χ§Χ™Χ™ΧΧ™Χ ...
    ];

    setPosts(dummyPosts);

    // π”— Χ”ΧΧ—Χ‘Χ¨Χ•Χ ΧΦΎSocket.IO
    const newSocket = io("http://localhost:5000");

    newSocket.on("connect", () => {
      console.log("π”— Χ—Χ™Χ‘Χ•Χ¨ ΧΦΎSocket Χ”Χ¦ΧΧ™Χ— ΧΧ”Χ‘ΧΧ•Χ’!");
    });

    // π§Ή Cleanup Χ‘ΧΆΧ Χ΅Χ’Χ™Χ¨Χ Χ”Χ§Χ•ΧΧ¤Χ•Χ Χ ΧΧ”
    return () => {
      newSocket.disconnect();
    };
  }, []); // Χ¨Χ™Χ¦Χ” Χ¤ΧΆΧ ΧΧ—Χ Χ‘ΧΧ‘Χ“ Χ‘ΧΧΆΧ™Χ Χ Χ”Χ§Χ•ΧΧ¤Χ•Χ Χ ΧΧ”

  return (
    <div className="blog-container">
      <h1>π“° Χ‘ΧΧ•Χ’ Χ”Χ’Χ•ΧΧ©Χ™Χ</h1>
      <p>Χ›ΧΧ ΧΧΧ¦ΧΧ• Χ›ΧΧ‘Χ•Χ, ΧΧ™Χ¤Χ™Χ, ΧΧ“Χ¨Χ™Χ›Χ™Χ Χ•Χ—Χ•Χ•Χ™Χ•Χ ΧΧ”ΧΆΧ•ΧΧ Χ”ΧΧ•Χ¤ΧΧ Χ©Χ Χ”Χ’ΧΧ™Χ©Χ” π</p>

      <div className="blog-grid">
        {posts.map((post) => (
          // β… Χ©Χ™ΧΧ•Χ© Χ‘-<div> Χ¨Χ’Χ™Χ ΧΧ›Χ¨ΧΧ™Χ΅ Χ›Χ“Χ™ Χ©Χ”-CSS Χ™ΧΆΧ‘Χ•Χ“ Χ›Χ¦Χ¤Χ•Χ™
          <div className="blog-card" key={post.id}>
            <img src={post.image} alt={post.title} className="blog-image" />
            <div className="blog-content">
              <h3>{post.title}</h3>
              <p className="blog-date">π“… {post.date}</p>
              <p>{post.description}</p>

              {/* β… 1. Χ§Χ™Χ©Χ•Χ¨ "Χ§Χ¨Χ ΧΆΧ•Χ“" Χ¨Χ’Χ™Χ (Χ›Χ Χ™Χ΅Χ” ΧΧ‘ΧΧ•Χ’) */}
              <Link to={`/blog/${post.id}`} className="read-more">
                Χ§Χ¨Χ ΧΆΧ•Χ“
              </Link>

              {/* β… 2. Χ”Χ§Χ™Χ©Χ•Χ¨ Χ”ΧΧ•ΧΧ Χ” ΧΧ“Χ™Χ•Χ (Χ›Χ Χ™Χ΅Χ” ΧΧΧ©Χ›Χ•Χ) */}
              {isLoggedIn && (
                <Link
                  to={`/blog/discussion/${post.id}`}
                  className="read-more discussion-link"
                  // ΧΧ©Χ Χ” ΧΆΧ™Χ¦Χ•Χ‘ Χ›Χ“Χ™ Χ©ΧΧ Χ™Χ‘ΧΧΆ ΧΧ Χ”Χ§Χ™Χ©Χ•Χ¨ Χ”Χ§Χ•Χ“Χ
                  style={{ marginRight: "10px", backgroundColor: "#005f86" }}
                >
                  π’¬ ΧΧ©Χ›Χ•Χ Χ“Χ™Χ•Χ
                </Link>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BlogPage;
