import axios from "axios";
import { useEffect, useState } from "react";

function HomePage() {
  const [forecast, setForecast] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const latitude = 32.08; // תחליף לקואורדינטות שלך
  const longitude = 34.78;

  const fetchForecast = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        "https://marine-api.open-meteo.com/v1/marine",
        {
          params: {
            latitude,
            longitude,
            hourly: "wave_height,wind_speed,wind_direction,sea_state",
          },
        }
      );

      setForecast(response.data);
    } catch (err) {
      console.error("שגיאה בטעינת תחזית הים:", err);
      setError("לא הצלחנו לטעון את התחזית. נסה שוב מאוחר יותר.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchForecast();
  }, []);

  if (loading) return <div>טוען תחזית...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>תחזית הים – דף בית</h1>
      <button onClick={fetchForecast} style={{ marginBottom: "20px" }}>
        רענן תחזית
      </button>

      <div>
        <h2>תחזית לשעות הקרובות</h2>
        <table border="1" cellPadding="8" cellSpacing="0">
          <thead>
            <tr>
              <th>שעה</th>
              <th>גובה גלים (מטר)</th>
              <th>מהירות רוח (קמ"ש)</th>
              <th>כיוון רוח (°)</th>
              <th>מצב הים</th>
            </tr>
          </thead>
          <tbody>
            {forecast.hourly.time.slice(0, 12).map((time, index) => (
              <tr key={time}>
                <td>
                  {new Date(time).toLocaleTimeString([], {
                    hour: "2-digit",
                    minute: "2-digit",
                  })}
                </td>
                <td>{forecast.hourly.wave_height[index]}</td>
                <td>{forecast.hourly.wind_speed[index]}</td>
                <td>{forecast.hourly.wind_direction[index]}</td>
                <td>
                  {forecast.hourly.sea_state
                    ? forecast.hourly.sea_state[index]
                    : "לא זמין"}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default HomePage;
